INSERT INTO `wp_2_posts` VALUES (1, 4, '2013-10-09 18:47:16', '2013-10-09 18:47:16', '<section>We are a team of researchers from University College London and The British Museum, and this is the platform where we will be discussing our current project <em><a href="http://micropasts.org/" title="Micropasts">Micropasts</a>  Crowd and Community-fuelled Archaeological Research</em>. We will post research updates and share ideas on public archaeology, community archaeology, crowdsourcing and crowdfunding. We hope that you will be interested in joining the discussion!</section><section></section><section><em>Andy, Chiara &amp; Dan</em></section>', 'Welcome to the discussion!', '', 'trash', 'open', 'open', '', 'hello-world', '', '', '2013-10-22 15:56:39', '2013-10-22 15:56:39', '', 0, 'http://research.micropasts.org/?p=1', 0, 'post', '', 0); 
INSERT INTO `wp_2_posts` VALUES (2, 4, '2013-10-09 18:47:16', '2013-10-09 18:47:16', 'We are a team of <a title="researchers" href="http://research.micropasts.org/people/">researchers</a> from University College London and The British Museum, and this is the platform where we will be discussing our current project <em><a title="Micropasts" href="http://micropasts.org/">Micropasts</a> - Crowd and Community-fuelled Archaeological Research</em>. We will post research updates and share ideas on public archaeology, community archaeology, crowdsourcing and crowdfunding. We hope that you will be interested in joining the discussion!', 'Welcome to the discussion!', '', 'trash', 'open', 'open', '', 'sample-page', '', '', '2013-10-23 11:19:52', '2013-10-23 11:19:52', '', 0, 'http://research.micropasts.org/?page_id=2', 0, 'page', '', 0); 
