INSERT INTO `wp_2_posts` VALUES (30, 2, '2013-10-18 14:48:54', '2013-10-18 14:48:54', 'We are a team of researchers from University College London and The British Museum, and this is the platform where we will be discussing our current project <em>Micropasts - Crowd and Community-fuelled Archaeological Research</em>. We will post research updates and share ideas on public archaeology, community archaeology, crowdsourcing and crowdfunding. We hope that you will be interested in joining the discussion!', 'Welcome to the discussion!', '', 'inherit', 'open', 'open', '', '2-revision-v1', '', '', '2013-10-18 14:48:54', '2013-10-18 14:48:54', '', 2, 'http://research.micropasts.org/2013/10/18/2-revision-v1/', 0, 'revision', '', 0); 
