INSERT INTO `wp_2_posts` VALUES (36, 2, '2013-10-18 15:05:00', '2013-10-18 15:05:00', 'Through this project, we hope to achieve five key objectives and particularly to:\r\n\r\n<strong>Enhance</strong> the degree to which traditional academics, other organised communities of archaeological interest, and a wider crowd of contributors can collaborate in the co-production of archaeological knowledge, using web technologies.\r\n\r\n<strong>Develop</strong> a long-term crowd-sourcing platform for co-producing archaeological open data.\r\n\r\n<strong>Capture</strong> research-quality, 3D information about large numbers of archaeological objects via participatory methods.\r\n\r\n<strong>Explore</strong>, through crowd-sourcing, the co-design and sustainable micro-funding of projects that arise out of existing community collaborations and utilise co-produced data.\r\n\r\n<strong>Scope</strong> what kinds of relationships in archaeology are forged or altered when established communities of interest, traditional academics and wholly new contributors collaborate in the above ways.', 'Our objectives', '', 'trash', 'open', 'open', '', 'our-objectives', '', '', '2013-10-24 20:00:33', '2013-10-24 20:00:33', '', 0, 'http://research.micropasts.org/?page_id=36', 0, 'page', '', 0); 
