INSERT INTO `wp_2_posts` VALUES (138, 2, '2013-10-25 18:13:31', '2013-10-25 18:13:31', '<strong>Project team</strong>\r\n\r\n<a title="Andrew Bevan" href="http://www.ucl.ac.uk/archaeology/people/staff/bevan">Andrew Bevan</a>, UCL Institute of Archaeology\r\n<em></em><em>Principal investigator\r\n</em>\r\n\r\n<a title="Daniel Pett" href="http://www.britishmuseum.org/about_us/departments/staff/portable_antiquities_treasure/daniel_pett.aspx">Daniel Pett</a>, The British Museum\r\n<em>Co-investigator</em>\r\n\r\n<a title="Rachael Sparks" href="http://www.ucl.ac.uk/archaeology/people/staff/sparks">Rachael Sparks</a>, UCL Institute of Archaeology\r\n<em>Co-investigator</em>\r\n\r\n<a title="Chiara Bonacchi" href="http://www.ucl.ac.uk/archaeology/people/staff/bonacchi">Chiara Bonacchi</a>, UCL Institute of Archaeology\r\n<em>Research Associate</em>\r\n\r\nAdi Keinan, UCL Institute of Archaeology\r\n<em>Research Associate</em>\r\n\r\n<a title="Ian Carroll" href="http://www.ucl.ac.uk/archaeology/people/staff/technical">Ian Carroll</a>,UCL Institute of Archaeology\r\n<i>Archival Collections Liaison</i>\r\n\r\n<a title="Neil Wilkin" href="http://www.britishmuseum.org/about_us/departments/staff/prehistory_and_europe/neil_wilkin.aspx">Neil Wilkin</a>, TheBritish Museum,Dept. of Prehistory and Europe\r\n<i>Archival Collections Liaison</i>\r\n\r\n&nbsp;\r\n\r\n<strong>Advisory group</strong>\r\n\r\n<a title="Roger Bland OBE" href="http://bit.ly/18FOY9R">Roger Bland</a>, The British Museum, Dept. of Prehistory and Europe\r\n\r\n<a title="Tim Causer" href="http://www.ucl.ac.uk/Bentham-Project/about/staff/causer">Tim Causer</a>, UCL Laws\r\n\r\n<a title="Nathalie Cohen" href="http://bit.ly/197RLp8">Nathalie Cohen</a>, Thames Discovery Project\r\n\r\n<a title="Stuart Dunn" href="http://bit.ly/1aock2l">Stuart Dunn</a>, King\'s College London, Digital Humanities\r\n\r\n<a title="Susie Green" href="http://bit.ly/17V5rWX">Susie Green</a>, UCL Institute of Archaeology\r\n\r\n<a title="Lorn